import React, { useState } from 'react';
import {
  Accordion,
  AccordionBody,
  AccordionHeader,
  AccordionItem,
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


const Karakterler=(props)=>{

const {veri}=props;


    const [open, setOpen] = useState('0');
    const toggle = (id) => {
      if (open === id) {
        setOpen();
      } else {
        setOpen(id);
      }
    };



return(
    <div>

{veri.map((item,index)=>{

    return(
    <Accordion open={open} toggle={toggle}>
                 <AccordionItem>
          <AccordionHeader targetId={index}>{item.name}</AccordionHeader>
          <AccordionBody accordionId={index}>
            <p>Gender:{item.gender}</p>
            <p>Height:{item.height}</p>
            <p>Mass:{item.mass}</p>
            <p>BirthYear:{item.birth_year}</p>
            <p>Eye Color:{item.eye_color}</p>
            <p>Hair ColorGender:{item.hair_color}</p>
            <p>Skin Color:{item.skin_color}</p>
            <p>Appears in {lenght(item.films)} films</p>
            
          </AccordionBody>
        </AccordionItem> 

    </Accordion>
        
    )
})}
    </div>
)

}

export default Karakterler;
